import { Component , OnInit } from '@angular/core';
import { SocialAuthService } from "@abacritt/angularx-social-login";
import { SocialUser } from "@abacritt/angularx-social-login";
import {ApiCallService} from "./services/api-call.service";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{

  constructor(private authService:SocialAuthService,
              private apiCall:ApiCallService
  )
  {}
  ngOnInit(): void {

        this.authService.authState.subscribe({

        next:(user)=>{
            alert("login sucsess")
            console.log(user);
            this.apiCall.loginWithGoogle(user).subscribe({
                next:(data:any)=>{
                    console.log("data from backend");
                    console.log(data);
                    this.token = data['jwtToken'];
                    console.log(this.token);

                },
                error:(error:any)=>{
                    console.log("error from backend")
                    console.log(error);

                }



            })



        },
        error:(error)=>{
            alert("login error")
            console.log(error)
        },
        complete:()=>{
            console.log("request completed")

        }

        })

    }

    getUsers(){
      this.apiCall.getUsers(this.token).subscribe({
      next:data=>{
          console.log(data);

      },
      error:error=>{
            console.log(error);
      }


      })

    }



  title = 'social-login';
  token=''



}

<div class="container">
  <div class="row mt-5">
    <div class="col-md-6 offset-md-3">
      <h1 class="text-center">Welcome to Social Login App </h1>
      <br />
      <asl-google-signin-button
        type='standard'
        size='large'
        data-text="Login with google"
        theme="filled_blue"
      ></asl-google-signin-button>
      <button (click)="getUsers()"  class="btn btn-success mt-2"> Get Users </button>


    </div>
  </div>
</div>
